					<script>
    document.addEventListener("DOMContentLoaded", () => {
    const scrollContainer = document.querySelector("#infobox-carousel > div > section > div");
    const itemList = document.querySelector("#infobox-carousel > div > section > div > ul");
    
    if (!scrollContainer || !itemList) return;
    let isDown = false;
    let startX;
    let scrollLeft;
    // Drag-to-Scroll
    scrollContainer.addEventListener("mousedown", (e) => {
        isDown = true;
        scrollContainer.classList.add("grabbing");
        startX = e.pageX - scrollContainer.offsetLeft;
        scrollLeft = scrollContainer.scrollLeft;
    });
    scrollContainer.addEventListener("mouseleave", () => {
        isDown = false;
        scrollContainer.classList.remove("grabbing");
    });
    scrollContainer.addEventListener("mouseup", () => {
        isDown = false;
        scrollContainer.classList.remove("grabbing");
    });
    scrollContainer.addEventListener("mousemove", (e) => {
        if (!isDown) return;
        e.preventDefault();
        const x = e.pageX - scrollContainer.offsetLeft;
        const walk = (x - startX) * 2;
        scrollContainer.scrollLeft = scrollLeft - walk;
    });
    // Pagination Dots
    const paginationContainer = document.createElement("div");
    paginationContainer.classList.add("pagination");
    scrollContainer.parentNode.appendChild(paginationContainer);
    function updatePagination() {
        paginationContainer.innerHTML = "";
        const items = itemList.children;
        const itemWidth = items[0].offsetWidth;
        const totalItems = items.length;
        const visibleItems = Math.floor(scrollContainer.clientWidth / itemWidth);
        const totalPages = Math.ceil(totalItems / visibleItems);
        for (let i = 0; i < totalPages; i++) {
            const dot = document.createElement("span");
            dot.classList.add("pagination-dot");
            if (i === Math.round(scrollContainer.scrollLeft / scrollContainer.clientWidth)) {
                dot.classList.add("active");
            }
            dot.addEventListener("click", () => {
                scrollContainer.scrollLeft = i * scrollContainer.clientWidth;
            });
            paginationContainer.appendChild(dot);
        }
    }
    // Update pagination when scrolling
    scrollContainer.addEventListener("scroll", updatePagination);
    // Initialize pagination on load
    updatePagination();
});
</script>				</div>
